import{_ as a,c as r,o as n,aj as t}from"./chunks/framework.218xjblo.js";const p=JSON.parse('{"title":"When (not) to use Handlebars?","description":"","frontmatter":{},"headers":[],"relativePath":"guide/installation/when-to-use-handlebars.md","filePath":"guide/installation/when-to-use-handlebars.md","lastUpdated":1695492225000}'),o={name:"guide/installation/when-to-use-handlebars.md"};function l(s,e,i,d,h,u){return n(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="when-not-to-use-handlebars" tabindex="-1">When (not) to use Handlebars? <a class="header-anchor" href="#when-not-to-use-handlebars" aria-label="Permalink to &quot;When (not) to use Handlebars?&quot;">​</a></h1><p>This section should give you an overview over the strengths and weaknesses of Handlebars. Ideally, it enables you to see if handlebars fits your use case and make an informed decision about choosing handlebars or not.</p><div class="info custom-block"><p class="custom-block-title">A note on using Handlebars in the browser</p><p>Handlebars is a pure rendering engine. It works well if you want to allow people to write templates for rendering HTML-pages, e-mails or markdown files.</p><p>It has <strong>no</strong> built-in support for <strong>event-handling</strong>, accessing <strong>backend-services</strong> or incremental <strong>DOM updates</strong>.</p><p>If you want to build a <strong>single-page application</strong> and you want to handle user-input, you should probably look for a framework like</p><ul><li><a href="https://angular.io/" target="_blank" rel="noreferrer">Angular</a></li><li><a href="https://aurelia.io/" target="_blank" rel="noreferrer">Aurelia</a></li><li><a href="https://emberjs.com/" target="_blank" rel="noreferrer">Ember</a></li><li><a href="https://infernojs.org/" target="_blank" rel="noreferrer">Inferno</a></li><li><a href="https://mithril.js.org/" target="_blank" rel="noreferrer">Mithril</a></li><li><a href="https://svelte.dev/" target="_blank" rel="noreferrer">Svelte</a></li><li><a href="https://ractive.js.org/" target="_blank" rel="noreferrer">Ractive</a></li><li><a href="https://reactjs.org/" target="_blank" rel="noreferrer">React</a></li><li><a href="https://vuejs.org/" target="_blank" rel="noreferrer">Vue</a></li></ul></div><h2 id="pro-handlebars" tabindex="-1">Pro Handlebars <a class="header-anchor" href="#pro-handlebars" aria-label="Permalink to &quot;Pro Handlebars&quot;">​</a></h2><ul><li>Handlebars is good for rendering in CLI-apps, non-HTML text content, server-side rendering of pure contents.</li><li>Handlebars has been ported to many programming languages (Java, Rust etc).</li></ul><h2 id="contra-handlebars" tabindex="-1">Contra Handlebars <a class="header-anchor" href="#contra-handlebars" aria-label="Permalink to &quot;Contra Handlebars&quot;">​</a></h2><ul><li>Handlebars is not good for fast (incremental) updates of the DOM, event-handling, frontend-backend communication.</li></ul>',7)])])}const g=a(o,[["render",l]]);export{p as __pageData,g as default};
